import React, { useState,useEffect } from 'react'
import {View,Text,StyleSheet, TextInput, TouchableOpacity,Modal} from 'react-native'
import {AntDesign} from '@expo/vector-icons'
import { Entypo } from '@expo/vector-icons';
import {initializeApp} from 'firebase/app'
import {firebaseConfig} from '../Components/Test'
import {addDoc,getFirestore,collection} from 'firebase/firestore'
import DatePicker from 'react-native-modern-datepicker'
import {getToday,getFormatedDate} from 'react-native-modern-datepicker'
const app=initializeApp(firebaseConfig)
const db=getFirestore(app)


export default function AddScreen({route,navigation}) {
    const [Title,setTitle]=useState()
    const [Content,setContent]=useState('')
    const [currentDate, setCurrentDate] = useState('');
    const [ideashow,setIdeashow]=useState(false)
    const [coding,setCoding]=useState(false)
    const [ideaData,setideaData]=useState('')
    const [codeData,setCodeData]=useState('')
    const [show,setshow]=useState(false)
    const [Dateshow,setDate]=useState('')
    useEffect(() => {
      var date = new Date().getDate(); //Current Date
      var month = new Date().getMonth() + 1; //Current Month
      var year = new Date().getFullYear(); //Current Year
      var hours = new Date().getHours(); //Current Hours
      var min = new Date().getMinutes(); //Current Minutes
      var sec = new Date().getSeconds(); //Current Seconds
      setCurrentDate(
        date + '/' + month + '/' + year 
        + ' ' + hours + ':' + min + ':' + sec
      );
    }, []);
    const HandelpostData = async () => {
      // Get the current user's UID (you should have this from your authentication)
      const userUID = route.params.userUID; // Replace with the actual UID
    
      if (Content !== undefined) {
        // Add a new note document to the user's subcollection
        const res = await addDoc(collection(db, `notes/${userUID}/user_notes`), {
          title: Title,
          content: Content,
          idea: ideaData,
          code: codeData,
          time: Dateshow
        });
    
        console.log('New note ID:', res.id);
    
        setTitle('');
        setContent('');
        setideaData('');
        setCodeData('');
      } else {
        console.log('Content is undefined. Check your state management and input handling.');
      }
    };
    
      
      const CLoseClear=()=>{
          
        setTitle('');
        setContent('');
        setideaData('',
        setCodeData(''))
         navigation.navigate('Home')
      }

      const ChangeIDea=()=>{
        setIdeashow(!ideashow)
      }
      const ChangeCoding=()=>{
        setCoding(!coding)
      }
      const handleDateSelect = (selectedDate) => {
        // Set the selected date
        setDate(selectedDate);
      
        // Filter the data based on the selected date
      }
      console.log(Dateshow)
      const today=new Date();
      const startDate=getFormatedDate(today.setDate(today.getDate()+1),'YYY/MMM/DDD')
  return (
  <View style={{flex:1,backgroundColor:'#befaf3',marginTop:0,alignItems:'center'}}>
    <View style={{marginTop:60,width:'100%',alignItems:'center'}}>
        <TextInput value={Title} onChangeText={(text)=>setTitle(text)}
         style={{borderWidth:1,padding:10,width:'90%',elevation:3,fontSize:22,
         
        borderColor:'white',borderRadius:0,backgroundColor:'#dff0aa'}}placeholder='Enter Title'/>
    </View>
    

    <Modal transparent={true} visible={show} >
        <View style={{ flex: 1, justifyContent: 'flex-end', alignItems: 'center',backgroundColor:'rgba(0,0,0,0.5)' }}>
          <View style={{ width: '100%', height: '50%', backgroundColor: 'white' , borderTopRightRadius: 30, borderTopLeftRadius: 30 }}>
         <View style={{margin:20}}>
         <View style={{alignItems:'flex-end',}}><AntDesign name='close' size={24} color='black'onPress={()=>setshow(false)}/></View>
           <View style={{width:'90%',margin:0,left:20}}>
            
            <DatePicker selected={Dateshow} maximumDate={startDate} onSelectedChange={(text)=>handleDateSelect(text)}/>
            </View>
            </View>
          </View>
        </View>
      </Modal>

    <View style={styles.TextInputView}>
    <TouchableOpacity style={{width:'70%'}} onPress={()=>setshow(true)} >
      <View style={{alignItems:'center',flexDirection:'row',justifyContent:'space-evenly'}}>
      <AntDesign name="calendar" size={24} color="black" />
      <TextInput placeholder='Put Date and Time Here'
       style={{borderWidth:1,width:'70%',padding:6,borderColor:'#ada4a3',borderRadius:6,margin:10}}value={Dateshow}onPressIn={()=>setshow(true)}/></View>
          </TouchableOpacity>
    <View style={{flexDirection:'row',alignItems:'center',justifyContent:'space-between',margin:0,}}>
    
          <TouchableOpacity onPress={ChangeIDea} 
          style={{width:'27%',backgroundColor:ideashow? '#d162c6' : '#b9d8ed',margin:10,borderBottomEndRadius:22,borderTopStartRadius:22}}>
          <Text style={{textAlign:'center',padding:10}} >Idea</Text></TouchableOpacity>
          <TouchableOpacity onPress={ChangeCoding} 
          style={{width:'27%',backgroundColor:coding? '#d162c6' : '#b9d8ed',margin:10,borderBottomEndRadius:22,borderTopStartRadius:22}}>
          <Text style={{textAlign:'center',padding:10}}>Coding</Text></TouchableOpacity>
      </View>
 
  

      {
  ideashow && coding ? (
    <View style={{ width: '100%' }}>
     
      <TextInput
        placeholder='Enter IDEA Here'
        value={ideaData}
        onChangeText={(text) => setideaData(text)}
        style={styles.TextInputStyleALL}
        multiline={true}
      />
      <TextInput
        placeholder='Enter code Here'
        value={codeData}
        onChangeText={(text) => setCodeData(text)}
        style={styles.TextInputStyleALL}
        multiline={true}
      />
    </View>
  ) : ideashow ? (
    <View style={{ width: '100%' }}>
      <TextInput
        placeholder='Enter Idea Here'
        value={ideaData}
        onChangeText={(text) => setideaData(text)}
        style={styles.TextInputStyleIdea}
        multiline={true}
      />
    
    </View>
  ) : coding ? (
    <View style={{ width: '100%' }}>
      <TextInput
        placeholder='Enter Text Here'
        value={codeData}
        onChangeText={(text) => setCodeData(text)}
        style={styles.TextInputStyleIdea}
        multiline={true}
      />
   
    </View>
  ) : (
    <TextInput
      placeholder='Enter text Here'
      value={Content}
      onChangeText={(text) => setContent(text)}
      style={styles.TextInputStyle}
      multiline={true}
    />
  )
}
    </View>
    <View style={{width:'80%',margin:20,flexDirection:'row',justifyContent:'space-between'}}>
    <TouchableOpacity style={{
        width:100,
        textAlign: 'center',
        fontSize: 18,
        color: 'white',
        fontWeight: 'bold',
         backgroundColor: '#2b692c',
          borderRadius: 10,
      }}
      onPress={()=>{HandelpostData(),navigation.navigate('Home')}}
     >
    <Text style={{padding:10,
      color:'white',
      fontWeight:'bold',
      fontSize:22,
      textAlign:'center'}}>
   Save
    </Text>
    </TouchableOpacity>
      <View style={{  }}>
    <TouchableOpacity style={{
        width:100,
        textAlign: 'center',
        fontSize: 18,
        color: 'white',
        fontWeight: 'bold',
         backgroundColor: '#8c160e',
          borderRadius: 10
      }}
      onPress={CLoseClear}
     >
    <Text style={{padding:10,
      color:'white',
      fontWeight:'bold',
      fontSize:22,
      textAlign:'center'}}>
   Close
    </Text>
    </TouchableOpacity>
  </View>
     
    </View>
    
  
  </View>
  )
}

const styles=StyleSheet.create({
    TextInputView:{
        width:'90%',
        height:'70%',
        backgroundColor:'#dff0aa',
        elevation:3,
        borderRadius:0,justifyContent:'flex-start',alignItems:'center',
       
    },
    TextInputStyle:{
        width:'100%',
        
        height:'75%',
        borderWidth:1,
        borderColor:'white',
        textAlignVertical:'top',
        fontSize:16,
        padding:10,borderRadius:20
      
        
    },
    TextInputStyleIdea:{
      width:'100%',
        margin:1,
        marginVertical:8,
      height:'83%',
      borderWidth:1,
      borderColor:'white',
      textAlignVertical:'top',
      fontSize:16,
      padding:10,borderRadius:20
    
    }
    ,TextInputStyleALL:{
      width:'100%',
      margin:1,
      marginVertical:8,
      height:'39%',
      borderWidth:1,
      borderColor:'white',
      textAlignVertical:'top',
      fontSize:16,
      padding:10,borderRadius:20
    
    }
})
