import React, { useState,useEffect } from 'react';
import { View, Text, StyleSheet, TextInput,Linking,Platform,Button, Modal, ScrollView  } from 'react-native';
import { AntDesign } from '@expo/vector-icons';
import { initializeApp } from 'firebase/app';
import { getAuth } from 'firebase/auth';
import { firebaseConfig } from '../Components/Test'; // Ensure this path is correct
import { doc, updateDoc,getFirestore,collection} from 'firebase/firestore';

import * as DocumentPicker from 'expo-document-picker';
import * as IntentLauncher from 'expo-intent-launcher';
import * as Print from 'expo-print';
import * as FileSystem from 'expo-file-system';
import { shareAsync } from 'expo-sharing';
import { printToFileAsync } from 'expo-print';
import {WebView} from 'react-native-webview'
import DatePicker from 'react-native-modern-datepicker'
import {getToday,getFormatedDate} from 'react-native-modern-datepicker'


const app = initializeApp(firebaseConfig);
const db = getFirestore(app);



  
export default function EditScreen({ route }) {

    const item = route.params.Data;

    console.log(item, '()()()');
    const userId = route.params.userUID;
    console.log(userId,'Welcome')
    const [Title, setTitle] = useState(item.title);
    const [Content, setContent] = useState(item.content);
    const [edit, setEdit] = useState();
    const [pdfname,setPdfname]=useState()
    const [pdfUri,setPdfURI]=useState()
    const [currentDate, setCurrentDate] = useState('');
    const [show,setShow]=useState(false)
    const [idea,setidea]=useState(item.idea)
    const [coding,setcoding]=useState(item.code)
    const [Dateshow,setDate]=useState(item.time)
    const [showTwo,setshowTwo]=useState(false)
    useEffect(() => {
      var date = new Date().getDate(); //Current Date
      var month = new Date().getMonth() + 1; //Current Month
      var year = new Date().getFullYear(); //Current Year
      var hours = new Date().getHours(); //Current Hours
      var min = new Date().getMinutes(); //Current Minutes
      var sec = new Date().getSeconds(); //Current Seconds
      setCurrentDate(
        date + '/' + month + '/' + year 
        + ' ' + hours + ':' + min + ':' + sec
      );
    }, []);

    console.log(item.time,'opopoppoppoopoopopo')
    console.log(pdfUri,'========//////////')
    const EditFunction = () => {
      setEdit(!edit);
    };
   const UpdateFunction = async () => {
  // Reference to the document you want to update
  const docRef = doc(collection(db, `notes/${userId}/user_notes`), item.id);

  // Update specific fields in the document
  try {
    await updateDoc(docRef, {
      title: Title,
      content: Content,
      idea:idea,
      code:coding,
      time:Dateshow
    });
    console.log('Document updated successfully');
  } catch (error) {
    console.error('Error updating document: ', error);
  }
};

const ModalHandler=()=>{
   setShow(!show)
}





const html = `
    <html>
      <body>
        <h1>${Title}</h1>
        <p style="color: red;">${Content}</p>
      </body>
    </html>
  `;

  const generatePdf = async () => {
    try {
      const file = await printToFileAsync({
        html: html,
        base64: true,
      });
      
      if (file && file.uri) {
        await shareAsync(file.uri);
        setPdfURI(file.uri);
      } else {
        console.error('File object or file.uri is null or undefined.');
      }
    } catch (error) {
      console.error('Error generating and sharing PDF:', error);
    }
  };
  
  console.log('ppppppppppppppppp',pdfUri)
  const downloadFromUrl = async () => {
    const filename = Title; // You might want to change the file extension to .pdf
    const result = await FileSystem.copyAsync({
      from: pdfUri, // Source file URI
      to: FileSystem.documentDirectory + filename, // Destination file URI
    });
  
    console.log(result);
  
    save(result, filename, "application/pdf");
  };
  

  const save = async (uri, filename, mimetype) => {
    if (!pdfUri) {
      console.error('pdfUri is null.');
      return;
    }
  
    if (Platform.OS === "android") {
      const permissions = await FileSystem.StorageAccessFramework.requestDirectoryPermissionsAsync();
      if (permissions.granted) {
        const base64 = await FileSystem.readAsStringAsync(pdfUri, { encoding: FileSystem.EncodingType.Base64 });
        await FileSystem.StorageAccessFramework.createFileAsync(permissions.directoryUri, filename, mimetype)
          .then(async (uri) => {
            await FileSystem.writeAsStringAsync(uri, base64, { encoding: FileSystem.EncodingType.Base64 });
          })
          .catch(e => console.log(e));
      } else {
        shareAsync(pdfUri);
      }
    } else {
      shareAsync(pdfUri);
    }
  };
  const handleDateSelect = (selectedDate) => {
    // Set the selected date
    setDate(selectedDate);
  
    // Filter the data based on the selected date
  }
  const { Data, userUID, backgroundColor } = route.params; // Access the color from the route.params
  console.log(Dateshow)
      const today=new Date();
      const startDate=getFormatedDate(today.setDate(today.getDate()+1),'YYY/MMM/DDD')
  return (
    <View style={{ flex: 1, margin: 0, alignItems: 'center',...styles.EditScreenContainer, backgroundColor}}>

<Modal transparent={true} visible={showTwo} >
        <View style={{ flex: 1, justifyContent: 'flex-end', alignItems: 'center',backgroundColor:'rgba(0,0,0,0.5)' }}>
          <View style={{ width: '100%', height: '50%', backgroundColor: 'white' , borderTopRightRadius: 30, borderTopLeftRadius: 30 }}>
         <View style={{margin:20}}>
