import React, { useState } from 'react'
import {View,Text,StyleSheet, TextInput} from 'react-native'
import {AntDesign} from '@expo/vector-icons'
import { Entypo } from '@expo/vector-icons';
import {initializeApp} from 'firebase/app'
import {firebaseConfig} from '../Components/Test'
import {addDoc,getFirestore,collection} from 'firebase/firestore'

const app=initializeApp(firebaseConfig)
const db=getFirestore(app)


export default function AddScreen({route}) {
    const [Title,setTitle]=useState()
    const [Content,setContent]=useState()

    const HandelpostData = async () => {
        // Get the current user's UID (you should have this from your authentication)
        const userUID =route.params.userUID; // Replace with the actual UID
      
        // Add a new note document to the user's subcollection
        const res = await addDoc(collection(db, `notes/${userUID}/user_notes`), {
          title: Title,
          content: Content,
        });
      
        console.log('New note ID:', res.id);
      
        setTitle('');
        setContent('');
      };
      
  return (
  <View style={{flex:1,margin:20,alignItems:'center'}}>
    <View style={{marginBottom:30,width:'100%',alignItems:'center'}}>
        <TextInput value={Title} onChangeText={(text)=>setTitle(text)}
         style={{borderWidth:1,padding:10,width:'90%',elevation:3,
        borderColor:'white',borderRadius:20,backgroundColor:'white'}}placeholder='Enter Title'/>
    </View>
    <View style={styles.TextInputView}>
    <TextInput value={Content} onChangeText={(text)=>setContent(text)}
    style={styles.TextInputStyle} multiline={true}
    placeholder='Enter Text Here'/>

    </View>
    <View style={{width:'80%',margin:20,flexDirection:'row',justifyContent:'space-between'}}>
    <AntDesign name="check" size={54} color="green"  onPress={HandelpostData}/>
    <Entypo name="cross" size={54} color="red" />
     
    </View>
    
  
  </View>
  )
}

const styles=StyleSheet.create({
    TextInputView:{
        width:'90%',
        height:'70%',
        backgroundColor:'white',
        elevation:3,
        borderRadius:20,justifyContent:'flex-start',alignItems:'flex-start',
       
    },
    TextInputStyle:{
        width:'100%',
        
        height:'100%',
        borderWidth:1,
        borderColor:'white',
        textAlignVertical:'top',
        fontSize:16,
        padding:10,borderRadius:20
      
        
    }
})
