import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';
import { AntDesign } from '@expo/vector-icons';
import { initializeApp } from 'firebase/app';
import { firebaseConfig } from '../Components/Test';
import { getAuth, onAuthStateChanged } from 'firebase/auth';
import {
  getDocs,
  getFirestore,
  doc,
  collection,
  query,
  deleteDoc,
} from 'firebase/firestore';
import { FlatList } from 'react-native';
import { Entypo } from '@expo/vector-icons';
import { onSnapshot } from 'firebase/firestore';
import { MaterialIcons } from '@expo/vector-icons';
import { createDrawerNavigator } from '@react-navigation/drawer';
import { useNavigation } from '@react-navigation/native';
import { TextInput } from 'react-native-gesture-handler';

const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
const auth = getAuth(app);

export default function Home({ navigation, route }) {
  const [showNotes, setShowNotes] = useState([]);
  const [originalNotes, setOriginalNotes] = useState([]); // New state variable for original data
  const [userUID, setUserUID] = useState(null);
  const [qury, setQury] = useState('');
  const navigationDR = useNavigation();

  const [cartColors] = useState([
    '#BE90FB',
    '#F788E2',
    '#F69864',
    '#F9C654',
    '#6DB3FB',
  ]);

  onAuthStateChanged(auth, (user) => {
    if (user) {
      setUserUID(user.uid);
    } else {
      setUserUID(null);
    }
  });

  useEffect(() => {
    if (userUID) {
      const q = query(collection(db, `notes/${userUID}/user_notes`));

      const unsubscribe = onSnapshot(q, (querySnapshot) => {
        const notesData = [];
        querySnapshot.forEach((doc) => {
          notesData.push({
            ...doc.data(),
            id: doc.id,
          });
        });
        setOriginalNotes(notesData); // Set original data
        setShowNotes(notesData); // Initialize showNotes with original data
      });

      return () => {
        unsubscribe();
      };
    }
  }, [userUID]);

  const deleteDocument = async (collectionName, documentId) => {
    try {
      const docRef = doc(db, `${collectionName}/${documentId}`);
      await deleteDoc(docRef);
      console.log('Document deleted successfully');
    } catch (error) {
      console.error('Error deleting document: ', error);
    }
  };

  useEffect(() => {
    // Filter the notes based on the query
    const newData = originalNotes.filter((item) =>
      item.title.toLowerCase().includes(qury.toLowerCase())
    );
    setShowNotes(newData);
  }, [qury, originalNotes]); // Add originalNotes as a dependency

  return (
    <View style={styles.AllViewStyle}>
      <View
        style={{
          width: '100%',
          height: '20%',
          backgroundColor: '#4559d9',
          borderBottomRightRadius: 40,
          borderBottomLeftRadius: 40,
        }}
      >
        <View style={styles.plusIcon}>
          <View
            style={{
              borderWidth: 1,
              width: '80%',
              marginLeft: 16,
              borderRadius: 10,
              borderColor: '#bccae0',
              backgroundColor: '#bccae0',
            }}
          >
            <TextInput
              value={qury}
              onChangeText={(text) => setQury(text)}
              style={{ width: '80%', padding: 10, fontSize: 20 }}
              placeholder="Search. . . ."
            />
          </View>
          <AntDesign
            name="plus"
            style={{ margin: 5, marginHorizontal: 10 }}
            size={40}
            color="white"
            onPress={() =>
              navigation.navigate('AddScreen', {
                userUID: userUID,
              })
            }
          />
        </View>
      </View>
      <FlatList
        data={showNotes}
        keyExtractor={(item) => item.id}
        renderItem={({ item, index }) => (
          <TouchableOpacity
            onPress={() => {
              navigation.navigate('EditScreen', {
                Data: item,
                userUID: userUID,
                backgroundColor: cartColors[index % cartColors.length],
              });
            }}
            style={{
              ...styles.CartView,
              backgroundColor: cartColors[index % cartColors.length],
              elevation: 4,
            }}
          >
            <View style={styles.cartContent}>
              <View style={styles.textContainer}>
                <View
                  style={{ flexDirection: 'row', top: 5, justifyContent: 'space-between' }}
                >
                  <Text style={styles.titleText}>{item.title}</Text>
                  <MaterialIcons
                    style={{ left: '70%', marginRight: 40, marginTop: 6 }}
                    name="delete"
                    size={24}
                    color="red"
                    onPress={() =>
                      deleteDocument(`notes/${userUID}/user_notes`, item.id, item.time)
                    }
                  />
                </View>
                <View style={{ marginVertical: 5 }}>
                  <Text numberOfLines={1} style={styles.contentText}>
                    {item.content}
                  </Text>
                  <Text style={styles.dateTimeText}>Date and Time: {item.time}</Text>
                </View>
              </View>
              <View style={styles.iconContainer}></View>
            </View>
          </TouchableOpacity>
        )}
        showsVerticalScrollIndicator={false}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  CartView: {
    width: 330,
    height: 120,
    backgroundColor: 'white',
    borderRadius: 10,
    margin: 11,
    shadowColor: 'black',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.2,
    shadowRadius: 4,
  },
  AllViewStyle: {
    flex: 1,
    alignItems: 'center',
  },
  cartContent: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 10,
  },
  textContainer: {
    width: '100%',
  },
  iconContainer: {
    margin: 20,
  },
  titleText: {
    padding: 7,
    fontSize: 18,
    fontWeight: 'bold',
    color: '#3e5945',
  },
  contentText: {
    fontSize: 16,
    color: '#5a5c56',
    marginBottom: 0,
    padding: 10,
  },
  dateTimeText: {
    fontSize: 14,
    color: 'gray',
    padding: 7,
  },
  plusIcon: {
    flex: 1,
    flexDirection: 'row',
    justifyContent: 'space-between',
    margin: 0,
    margin: 15,
    alignItems: 'flex-end',
  },
});
