import React,{useState} from 'react'
import { Text, View,StyleSheet, TextInput } from 'react-native'
import { initializeApp } from 'firebase/app';
import { getAuth, createUserWithEmailAndPassword, signInWithEmailAndPassword } from 'firebase/auth';
import { firebaseConfig } from '../Components/Test'; // Ensure this path is correct

const app = initializeApp(firebaseConfig);
const auth = getAuth(app);

export default function SignUp({navigation}) {
  
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [UserName,setUserName]=useState('')
  const handleSignUp = () => {
    createUserWithEmailAndPassword(auth, email, password,UserName)
      .then((userCredential) => {
        const user = userCredential.user;
       alert("User Has been successful Add")
       navigation.navigate('Login',{
        email:email,
       
      })
      setEmail('')
      setPassword('')
      })
      .catch((error) => {
        console.error('Error:', error);
        alert('User Has not successful Add')
      });
  };

  return (
    <View style={styles.HeaderView}>
        <View style={styles.HeaderViewTxt}>
          <Text style={styles.HeaderTxt}>Hi, Welcome! ðŸ‘‹</Text>
          </View>
          <View style={styles.marginInput}> 
            <Text style={styles.InputLabelStyle}>User Name</Text>
            <View style={styles.ViewInputStyle}>
              <TextInput style={styles.TextInputStyle}
              value={UserName}
              onChangeText={(text)=>setUserName(text)} />
            </View>

          </View>

          <View style={styles.marginInput}> 
            <Text style={styles.InputLabelStyle}>Email</Text>
            <View style={styles.ViewInputStyle}>
              <TextInput style={styles.TextInputStyle} value={email} 
              onChangeText={(e)=>setEmail(e)}/>
            </View>

          </View>

          <View style={styles.marginInput}> 
            <Text style={styles.InputLabelStyle}>Password</Text>
            <View style={styles.ViewInputStyle}>
              <TextInput style={styles.TextInputStyle} value={password} 
              onChangeText={(e)=>setPassword(e)}/>
            </View>
            
          </View>
       
       <View style={styles.BtnStyle} >
        <Text style={styles.BtnTextStyle} onPress={handleSignUp}>SignUp</Text>
       </View>
          <View style={styles.lastText}>
            <Text style={styles.AlreadyText}>Already have an account ?</Text>
            <Text style={styles.LoginTextStyle}
            onPress={()=>navigation.navigate('Login')}>Login</Text>
          </View>
    </View>
  )
}

const styles=StyleSheet.create({
  HeaderView:{
    flex:1,
    backgroundColor:'white',
    justifyContent:'center',
    alignItems:'center'
  },
  HeaderViewTxt:{
    marginBottom:20
  },
  HeaderTxt:{
    fontSize:22,
    fontWeight:'bold'
  },
  InputLabelStyle:{
    fontSize:18,
    fontWeight:'bold',
    color:'#00004D',
    marginBottom:10
  },
  ViewInputStyle:{
    borderWidth:1,
    borderRadius:7,
    borderColor:'#C6C6C6'
  },
  
  TextInputStyle:{
    width:300,
    padding:7
  },
  BtnStyle:{
    backgroundColor:'#4559d9',
    width:'90%',
    borderRadius:10,
    marginVertical:30},
    BtnTextStyle:{
      padding:10,
      color:'white',
      fontSize:22,
      fontWeight:'bold',
      textAlign:'center'
    },
    lastText:{
      flexDirection:'row'
    },
    LoginTextStyle:{
      fontSize:18,
      fontWeight:'bold',
      color:'#160062'},
      AlreadyText:{
        margin:3
      },
      marginInput:{marginVertical:10}
})
