import React, { useState } from 'react'
import { Text, View, StyleSheet, TextInput } from 'react-native'
import SignUp from './SignUp'
import { initializeApp } from 'firebase/app';
import { getAuth, createUserWithEmailAndPassword, signInWithEmailAndPassword } from 'firebase/auth';
import { firebaseConfig } from '../Components/Test'; // Ensure this path is correct

const app = initializeApp(firebaseConfig);
const auth = getAuth(app);
export default function Login({ navigation }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSignIn = () => {
    signInWithEmailAndPassword(auth, email, password)
      .then(() => {
        alert("User Has been successful Add")
        navigation.navigate('Home')
        setEmail('')
        setPassword('')
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };



  return (
    <View style={styles.HeaderView}>
      <View style={styles.HeaderViewTxt}>
        <Text style={styles.HeaderTxt}>Hi, Wecome Back! ðŸ‘‹</Text>
      </View>
      <View>
        <Text style={styles.InputLabelStyle}>Email</Text>
        <View style={styles.ViewInputStyle}>
          <TextInput style={styles.TextInputStyle} value={email}
            onChangeText={(e) => setEmail(e)} />
        </View>

      </View>

      <View style={styles.ViewInStyle}>
        <Text style={styles.InputLabelStyle}>Password</Text>
        <View style={styles.ViewInputStyle}>
          <TextInput style={styles.TextInputStyle} value={password}
            onChangeText={(e) => setPassword(e)} />
        </View>

      </View>

      <View style={styles.BtnStyle}>
        <Text style={styles.BtnStyleText} onPress={handleSignIn}>Login</Text>
      </View>
      <View style={{ flexDirection: 'row' }}>
        <Text style={styles.lastText}>Donâ€™t have an account ?</Text>
        <Text style={styles.styleSignUp}
          onPress={() => navigation.navigate('SignUp')}>SignUp</Text>
      </View>
    </View>
  )
}

const styles = StyleSheet.create({
  HeaderView: {
    flex: 1,
    backgroundColor: 'white',
    justifyContent: 'center',
    alignItems: 'center'
  },
  HeaderViewTxt: {
    marginBottom: 20
  },
  HeaderTxt: {
    fontSize: 22,
    fontWeight: 'bold'
  },
  InputLabelStyle: {
    fontSize: 18,
    fontWeight: 'bold',
    color: '#00004D',
    marginBottom: 10
  },
  ViewInputStyle: {
    borderWidth: 1,
    borderRadius: 7,
    borderColor: '#C6C6C6'
  },

  TextInputStyle: {
    width: 300,
    height: 40,
  },
  BtnStyle: {
    backgroundColor: '#351A96',
    width: '90%',
    borderRadius: 10,
    marginVertical: 30
  },
  BtnStyleText: {
    padding: 10,
    color: 'white',
    fontSize: 22,
    fontWeight: 'bold',
    textAlign: 'center'
  },
  styleSignUp: {
    fontSize: 18,
    fontWeight: 'bold',
    color: '#160062'
  },
  lastText: {
    margin: 3
  },
  ViewInStyle: {
    marginVertical: 30
  }
})
