  }
  return (
    <View style={{flex:1,justifyContent:'center',alignItems:'center'}}>
      <View style={{width:'100%',alignItems:'center',}}>
        <Text>Its Your Text</Text>
        <TextInput value={title} onChangeText={(text)=>setTitle(text)} placeholder='Title Enter only'
        style={{borderWidth:1,width:'90%',margin:10,padding:10,borderRadius:20}}/>
        <TextInput style={{borderWidth:1,width:'90%',margin:10,padding:10,borderRadius:20}}/> 
        <View style={{width:150,margin:10}}>
        <Button title='Submit' color='pink'onPress={HandleAddData}/>
        </View>
      </View>
    </View>
  )
}
