import React, { useEffect, useState } from 'react'
import { View, Text, TextInput, ScrollView, Modal } from 'react-native'
import { FontAwesome, AntDesign } from '@expo/vector-icons'

const Data = [
    { id: 1, Work: 'Jogging'},
    { id: 2, Work: 'office' },
    { id: 3, Work: 'crikect'},
    { id: 4, Work: 'Waliball'},
    { id: 5, Work: 'Gym' },
    { id: 6, Work: 'FootBall'},
    { id: 7, Work: 'Water'},
    { id: 8, Work: 'Food' },
    { id: 9, Work: 'Hair Cuting'},
    { id: 10, Work: 'School'},
    { id: 11, Work: 'Collage' },
    { id: 12, Work: 'Bell'},
   
]

export default function App() {
    const [deleteData, setDelete] = useState(Data)
    const [query, setquery] = useState('')
    const [show,setshow]=useState(false)
    const [modaltxt,setmodaltxt]=useState('')
    const [modalSearchQuery, setModalSearchQuery] = useState('');
    const [modalSearch,setmodalSearch]=useState(Data)
   const  [newmodal,setnewmodal]=useState()
    const handleModalData = () => {
  if (modaltxt.trim() !== '') {
    const newItem = {
      id: deleteData.length + 1,
      Work: modaltxt,
    };
    setDelete([...deleteData, newItem]);
    setshow(false);
    setmodaltxt('');
    // Clear the modal search query when adding an item
    setModalSearchQuery('');
  }
}

    const handleDelete = (itemId) => {
        const newData = deleteData.filter((item) => item.id !== itemId)
        setDelete(newData)
    }
const ModalHandle=()=>{
    setshow(!show)
}
    const handleEdit = (itemId) => {
        const newData = deleteData.map((item) => {
            if (item.id === itemId) {
                return { ...item, isEditing: true }
            }
            return item
        })
        setDelete(newData)
    }

    const handleSave = (itemId, editedText) => {
        const newData = deleteData.map((item) => {
            if (item.id === itemId) {
                return { ...item, isEditing: false, Work: editedText }
            }
            return item
        })
        setDelete(newData)
    }
    useEffect(() => {
        if (query === '') {
            setDelete(Data)
            setmodalSearch(false)
        }
        else {
            const newData = deleteData.filter((item) =>
                item.Work.toLowerCase().includes(query.toLowerCase()))
            setDelete(Data)
            setmodalSearch(true)
        
        }

    }, [query])
    useEffect(() => {
        if (query === '') {
            setnewmodal(Data)
            setmodalSearch(false)
        }
        else {
            const newData = deleteData.filter((item) =>
                item.Work.toLowerCase().includes(query.toLowerCase()))
            setnewmodal(newData)
            setmodalSearch(true)
        
        }

    }, [query])
    const SeacrhText = (text) => {
        setquery(text)
        if (query === '') {
            setDelete(Data)
           
        }
        else {
            const newData = deleteData.filter((item) =>
            item.Work.toLowerCase().includes(query.toLowerCase()))
            setDelete(newData)
            setmodalSearch(false)
        
           
            console.log(newData)

        }
    }
    const SeacrhTextClick = () => {
        
        if (query === '') {
            setDelete(Data)
           
        }
        else {
            const newData = deleteData.filter((item) =>
            item.Work.toLowerCase().includes(query.toLowerCase()))
            setDelete(newData)
            setmodalSearch(false)
            setquery(newData[0].Work)
           
            

        }
    }
 
    return (

        <View style={{ flex: 1 }}>
            <Modal transparent={true}visible={show}>
                <View style={{flex:1,justifyContent:'center',alignItems:'center',backgroundColor:'rgba(0,0,0,0.5)'}}>
                   <View style={{width:'90%',height:'50%',backgroundColor:'white',elevation:4,
                   borderRadius:30}}>
                   <View style={{padding:40,alignItems:'center',justifyContent:'center',flex:1}}>
                    <Text style={{margin:22,fontSize:22,fontWeight:'900'}}>Enter Work</Text>
                    <TextInput value={modaltxt}
                    onChangeText={(text)=>setmodaltxt(text)}
                     style={{borderWidth:1,padding:10
                        ,width:'100%',borderRadius:20,borderColor:'#9c9695'}}/>
                     <View style={{margin:20,flexDirection:'column',alignItems:'center',justifyContent:'space-between'}}>
                        <Text style={{fontSize:20,margin:10,fontWeight:'900'}} onPress={handleModalData}>Add Now</Text>
                        <Text style={{fontSize:20,margin:10,fontWeight:'900'}}
                        onPress={ModalHandle}>Close Now</Text>
                     </View>
                   </View>
                    </View>
                </View>
            </Modal>
            <View style={{
                width: '100%', height: 150, backgroundColor: '#c879e0', borderBottomEndRadius: 60,
                borderBottomStartRadius: 60,
                flexDirection: 'row', alignItems: 'center', justifyContent: 'center'
            }}>
                <Text style={{ fontSize: 22, fontWeight: 'bold', margin: 0, color: '#d5f520' }}>Welcome to Saving Daily Work</Text>

            </View>
            <View style={{ paddingTop: 20, justifyContent: 'center', alignItems: 'center' }}>
                <View style={{
                    width: '70%', padding: 10, flexDirection: 'row',
                    elevation: 2, padding: 10, borderRadius: 15, justifyContent: 'space-between'
                }}>

                    <TextInput style={{ width: '60%', paddingLeft: 10 }} value={query}
                        onChangeText={(text) => setquery(text)} 
                        onPressIn={()=>setmodalSearch(true)}/>
                    <AntDesign name="search1" size={24} color="#bec4c0"  onPress={(text)=>SeacrhTextClick(text)}/>
                </View>
                {modalSearch &&query !==''?<View style={{width:'70%',borderRadius:15,
                height:200,backgroundColor:'white',elevation:2}}>
                {newmodal.map((item)=>
                <View style={{borderBottomWidth:1}}>
                    <Text style={{fontSize:22,padding:10,margin:10}} onPress={()=>SeacrhText(item.Work)}>{item.Work}</Text>
                    </View>)}
                </View>:null}
                <View style={{ width: '85%', top: '3%', margin: 10, flexDirection: 'row', justifyContent: 'space-between' }}>

                    <Text style={{ fontSize: 22, fontWeight: '900' }}>Working</Text>
                    <AntDesign name='plus' size={36} color='green' onPress={ModalHandle}/>
                </View>
                <ScrollView style={{width:'100%' }}>
                    {deleteData.map((item) => (

                        <View
                            key={item.id}
                            style={{
                                width: '90%',
                                backgroundColor: 'white',
                                elevation: 4,
                                flexDirection: 'row',
                                padding: 20,
                                margin: 20,
                                justifyContent: 'space-between',
                                alignItems: 'center',
                                borderRadius: 10,
                            }}
                        >
                            {item.isEditing ? (
                                <TextInput placeholder='Enter Work Name'
                                    style={{ borderWidth: 1, width: '50%', padding: 5, borderRadius: 10 }}
                                    value={item.Work} // Bind the input value to the Work state
                                    onChangeText={(text) => {
                                        const newData = deleteData.map((dataItem) => {
                                            if (dataItem.id === item.id) {
                                                return { ...dataItem, Work: text };
                                            }
                                            return dataItem;
                                        });
                                        setDelete(newData);
                                    }}
                                />
                            ) : (
                                <Text style={{ fontSize: 18, fontWeight: '700' }}>{item.Work}</Text>
                            )}

                            <View style={{ flexDirection: 'row', justifyContent: 'space-between', width: '25%' }}>
                                {item.isEditing ? (
                                    <AntDesign
                                        name="check"
                                        size={24}
                                        color="green"
                                        onPress={() => handleSave(item.id, item.Work)}
                                    />
                                ) : (
                                    <FontAwesome name="edit" size={24} color="green" onPress={() => handleEdit(item.id)} />
                                )}
                                <AntDesign name="delete" size={24} color="red" onPress={() => handleDelete(item.id)} />
                            </View>
                        </View>


                    ))}
                </ScrollView>
            </View>
        </View>

    )
}
